const email = $json.query?.email || $json.email;
if (!email) throw new Error('Email nao informado');

const token = Date.now().toString(36) + Math.random().toString(36).slice(2,10);
const ttlMinutes = 5;
const now = Date.now();
const expiresAt = now + ttlMinutes * 60 * 1000;

return [{ json: { email, token, expiresAt, createdAt: now, ttlMinutes } }];

