const tokenRow = $items('LKP_TOKEN')[0]?.json;
if (!tokenRow) {
  throw new Error('Token nao encontrado');
}
if (tokenRow.used === 'TRUE' || tokenRow.used === true) {
  throw new Error('Token ja usado');
}

// Tentativa de correção se for segundos:
if (Number(tokenRow.expiresAt) * 1000 < Date.now()) {
    throw new Error('Token expirado');
}

return [{ json: tokenRow }];

// normaliza para comparar "sem frescura"
const norm = s => (s ?? '').toString().trim().toLowerCase();

// o email que veio do KEEP_TOKEN
const alvo = norm($node['KEEP_TOKEN'].json.email);

// items = todas as linhas que o GET_ALUNOS trouxe
const linha = items.find(i =>
    norm(i.json.Email) === alvo || norm(i.json.email) === alvo
  );

// versão com tratamento: sempre retorna 1 item e diz se achou
if (!linha) {
  return [{json: { ok: false, error: 'ALUNO_NAO_ENCONTRADO', emailProcurado: $node['KEEP_TOKEN'].json.email }}];
}

// inclui ok:true e mantém todos os campos da linha
return [{ json: { ok:true, ...linha.json } }];

