// pega a query enviada pelo formulário (/webhook/api/alunos?q=...)
const qRaw = String($node['Webhook GET']?.json?.query?.q || '').trim();
if (!qRaw) {
  return [{ json: { results: [] } }];
}

// normaliza (minúsculas, remove acentos)
const normalize = s => String(s || '').toLowerCase().normalize('NFD').replace(/\p{Diacritic}/gu, '');

// divide em termos
const terms = qRaw.split(/\s+/).map(normalize);

// pega as linhas do sheet (GET_ALUNOS1)
const rows = $items('GET_ALUNOS1').map(i => i.json || {});
const take = 20;

// filtra
const matches = rows.filter(r => {
  const nome  = normalize(r['Aluno'] || r['aluno'] || '');
  const turma = normalize(r['Turma'] || r['turma'] || '');
  const mail  = normalize(r['Email'] || r['email'] || '');
  const all = `${nome} ${turma} ${mail}`;
  // exige que todos os termos digitados estejam presentes
  return terms.every(t => all.includes(t));
}).slice(0, take);

// monta resultado para o front (label e email)
const results = matches.map(r => ({
  email: r['Email'] || r['email'] || '',
  label: `${r['Aluno'] || r['aluno'] || ''} — ${r['Turma'] || r['turma'] || ''}`
}));

return [{ json: { results } }];

parameters.responseBody:
={{ ( () => {
  const apiURL = 'http://localhost:5678/webhook/api/alunos';
  const action = 'http://localhost:5678/webhook/qr/new';
  return `
<!doctype html>
<html>
<head>
<meta charset="utf-8" />
<meta name="viewport" content="width=device-width, initial-scale=1.0" />
<title>Gerar QR</title>
<style>
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }

  :root {
    --bg: #0f172a;
    --panel: #111827;
    --text: #e2e8f0;
    --muted: #94a3b8;
    --brand: #2563eb;
    --hover: #1e293b;
    --border: #334155;
  }

  body {
    background: var(--bg);
    color: var(--text);
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif;
    min-height: 100vh;
    padding: 16px;
  }

  .wrap {
    max-width: 720px;
    width: 100%;
    margin: 0 auto;
    padding: 32px 24px;
    background: var(--panel);
    border-radius: 16px;
    box-shadow: 0 12px 40px rgba(0, 0, 0, 0.35);
  }

  h1 {
    margin: 0 0 8px;
    font-size: 28px;
    font-weight: 600;
  }

  .sub {
    color: var(--muted);
    margin: 0 0 24px;
    font-size: 15px;
    line-height: 1.5;
  }

  .input-wrapper {
    position: relative;
    width: 100%;
    margin-bottom: 12px;
  }

  input {
    width: 100%;
    font-size: 16px;
    padding: 14px 16px;
    border-radius: 10px;
    border: 2px solid var(--border);
    background: #0b1220;
    color: var(--text);
    transition: all 0.2s ease;
    outline: none;
  }

  input:focus {
    border-color: var(--brand);
    box-shadow: 0 0 0 3px rgba(37, 99, 235, 0.1);
  }

  input::placeholder {
    color: #64748b;
  }

  ul#resultados {
    list-style: none;
    margin: 0;
    padding: 0;
    border-radius: 10px;
    overflow: hidden;
    background: #0b1220;
  }

  ul#resultados:empty {
    display: none;
  }

  li.sug {
    padding: 14px 16px;
    border-bottom: 1px solid var(--hover);
    cursor: pointer;
    transition: all 0.15s ease;
    font-size: 15px;
    line-height: 1.5;
  }

  li.sug:last-child {
    border-bottom: none;
  }

  li.sug:hover {
    background: var(--hover);
    padding-left: 20px;
  }

  li.sug:active {
    background: #0f172a;
  }

  /* Responsividade para celular */
  @media (max-width: 768px) {
    body {
      padding: 12px;
    }

    .wrap {
      padding: 24px 20px;
      border-radius: 12px;
      margin-top: 20px;
    }

    h1 {
      font-size: 24px;
    }

    .sub {
      font-size: 14px;
      margin-bottom: 20px;
    }

    input {
      font-size: 16px; /* Manter 16px para evitar zoom no iOS */
      padding: 12px 14px;
    }

    li.sug {
      padding: 12px 14px;
      font-size: 14px;
    }
  }

  @media (max-width: 480px) {
    .wrap {
      padding: 20px 16px;
    }

    h1 {
      font-size: 22px;
    }

    input {
      padding: 12px;
    }
  }

  /* Melhorias para toque em dispositivos móveis */
  @media (hover: none) and (pointer: coarse) {
    li.sug {
      padding: 16px;
      font-size: 15px;
    }

    li.sug:hover {
      padding-left: 16px; /* Remove animação no hover para mobile */
    }

    li.sug:active {
      background: var(--brand);
      color: white;
    }
  }
</style>
</head>
<body>
  <div class="wrap">
    <h1>Gerar QR de advertência</h1>
    <p class="sub">Digite o nome, turma ou e-mail do aluno:</p>
    <div class="input-wrapper">
      <input type="text" id="busca" autocomplete="off" placeholder="ex: maria@colegio..." />
    </div>
    <ul id="resultados"></ul>
  </div>
<script>
const busca = document.getElementById('busca');
const lista = document.getElementById('resultados');
const api = '${apiURL}';
const action = '${action}';

busca.addEventListener('input', async () => {
  const q = busca.value.trim();
  if (!q) { 
    lista.innerHTML = ''; 
    return; 
  }

  try {
    const res = await fetch(api + '?q=' + encodeURIComponent(q));
    if (!res.ok) throw new Error('HTTP ' + res.status);
    const dados = await res.json();

    if (dados.length === 0) {
      lista.innerHTML = '<li class="sug" style="cursor: default; color: var(--muted);">Nenhum aluno encontrado</li>';
      return;
    }

    lista.innerHTML = dados.map(d =>
      '<li class="sug" data-email="' + d.email + '">' + d.label + '</li>'
    ).join('');
  } catch (err) {
    console.error('Falha na API:', err);
    lista.innerHTML = '<li class="sug" style="cursor: default; color: #ef4444;">❌ Não foi possível buscar alunos.</li>';
  }
});

lista.addEventListener('click', e => {
  const li = e.target.closest('.sug');
  if (!li || !li.dataset.email) return;
  const email = li.dataset.email;
  window.location.href = action + '?email=' + encodeURIComponent(email);
});
</script>
</body>
</html>`;
})() }}
